machine_start_gcode.default_value:
;---------------------------------------
; ; ; Jellybox Start Script Begin ; ; ;
;_______________________________________
; M92 E140 ;optionally adjust steps per mm for your filament

;                 Print Settings Summary
; (overwriting these values will NOT change your printer's behavior)
;              sliced for:  {machine_name}
;         nozzle diameter:  {machine_nozzle_size}
;       filament diameter:  {material_diameter}
;            layer height:  {layer_height}
;        1st layer height:  {layer_height_0}
;              line width:  {line_width}
;          wall thickness:  {wall_thickness}
;          infill density:  {infill_sparse_density}
;          infill pattern:  {infill_pattern}
;       print temperature:  {material_print_temperature}
;  heated bed temperature:  {material_bed_temperature}
;       regular fan speed:  {cool_fan_speed_min}
;           max fan speed:  {cool_fan_speed_max}
;                 support?  {support_enable}
;              spiralized?  {magic_spiralize}

M117 Preparing        ;write Preparing
M140 S{material_bed_temperature}              ;set bed temperature and move on
M104 S{material_print_temperature}             ;set extruder temperature and move on
M206 X10.0 Y0.0       ;set x homing offset for default bed leveling
G21                   ;metric values
G90                   ;absolute positioning
M107                  ;start with the fan off
M82                   ;set extruder to absolute mode
G28                   ;home all axes
M203 Z5               ;slow Z speed down for greater accuracy when probing
G29                   ;auto bed leveling procedure
M203 Z7               ;pick up z speed again for printing
M190 S{material_bed_temperature}              ;wait for the bed to reach desired temperature
M109 S{material_print_temperature}             ;wait for the extruder to reach desired temperature
G92 E0                ;reset the extruder position
G1 F200 E5            ;extrude 5mm of feed stock
G92 E0                ;reset the extruder position again
M117 Print starting   ;write Print starting
;---------------------------------------------
; ; ; Jellybox Printer Start Script End ; ; ;
;_____________________________________________
